angular.module("todoApp",[]).controller("TodoListController",function(){var todoList=this;todoList.todos=[{text:"learn AngularJS",done:!0},{text:"build an AngularJS app",done:!1}],todoList.addTodo=function(){todoList.todos.push({text:todoList.todoText,done:!1}),todoList.todoText=""},todoList.remaining=function(){var count=0;return angular.forEach(todoList.todos,function(todo){count+=todo.done?0:1}),count},todoList.archive=function(){var oldTodos=todoList.todos;todoList.todos=[],angular.forEach(oldTodos,function(todo){todo.done||todoList.todos.push(todo)})}});